package Programming;

public class ReverseWorldprogramming {
  
	  // Reverse String  Programming 
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String A ="Kalpesh";
		
		for ( int i=A.length()-1 ;i>=0 ; i--)
			
		{
			System.out.print(A.charAt(i));
		}
		
		
	}

}
