package Z_Programming;

public class Star_programming {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//
//   //Case1
		for(int i=0;i<=5;i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print("*");
			}

			System.out.println();
		}

	
		for(int i=0;i<=5;i++)
		{
				for(int j=5;j>=i;j--)
				{				System.out.print("*");
			}
	
			System.out.println();
	
	
	
	
	}

	}}
