package Z_Programming;

public class Swaping_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// swaping Numbers
		
		 int a=10 ,b=20;
		 
		 System.out.println("before swapping number:"+a+"  " +b);
			
		  int c=a;//c=10
		  
		  a=b;//a=20
		  b=c;//b=10
			
		  System.out.println("After swapping number:"+a+"  " +b);
			
		//  case2	
		  
		  int A=10 , B=20;
			
       System.out.println("before swapping number:"+A+"  " +B);
		
       A=A+B; //A=30
      
       B=A-B; //30-20=10
       A=A-B; //30-10=20
		 
		 
	    
		 System.out.println("After swapping number:"+A+"  " +B);
	
	
	
	}

}
