package a_Test_Day4_Iterative_Statements;

public class a_Test3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	 
		  //Iterative Statement 3)for loop
		

		for (int a=2;a<=100;a++)    // for (initilization ,condition,Increment)
	
	    {System.out.println(a);}
	
		
		System.out.println("----------------");
	
		for (int a=2;a<=100;a+=2)    //for (initilization ,condition, Asssignment operator)
	
	    {System.out.println(a);}
	
		System.out.println("----------------");
	
		//for loop
				for(int x=15;x<=500;x*=5)
				{System.out.println(x);}
	
	
	
	
	
	
	}

}
